pragma solidity ^0.4.24;

import "../math/SafeMath.sol";
import "./ValidatorsManagerContract.sol";

contract ERC20Token {
  function transfer(address to, uint256 value) public returns (bool);
}

contract Gateway is ValidatorsManagerContract {

  using SafeMath for uint256;

  // From user to ERC20 balance
  mapping (address => uint256) balances;
  // ERC20 contract address (HEY token)
  ERC20Token tokenContract;

  event ERC20Withdrawn(address indexed owner, uint256 value);

  constructor (address _tokenContractAddress, address[] _validators, uint8 _threshold_num, uint8 _threshold_denom)
    public
    ValidatorsManagerContract(_validators, _threshold_num, _threshold_denom)
  {
    tokenContract = ERC20Token(_tokenContractAddress);
  }

  function withdrawERC20(uint256 amount, bytes sig)
    external
    isVerifiedByValidator(amount, address(tokenContract), sig)
  {
    // This transfer transaction depletes the ERC20 balance of the Gateway
    // contract itself - using the balance collected via previous deposits
    // from the user requesting the current withdrawal.
    tokenContract.transfer(msg.sender, amount);
    emit ERC20Withdrawn(msg.sender, amount);
  }
}
